import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import '../css/App.css';
import {ButtonContainer} from './Button';
import Logo from '../Logo icon.png';
import {FaRegHeart, FaSearch} from 'react-icons/fa'
class Navbar extends Component {
    render() {
       const srch = document.querySelector(".srch");
       const btn = document.querySelector('.srch-btn');
       const input = document.querySelector('.input')
        return (
            <div>
            {/* navigation bar */}
            <nav className="navbar navbar-expand-lg navbar-light bg-light px-sm-5">
                {/* This is my nav bar logo component */}
                <div className="container-fluid">
                    <Link to="/">
                       <img src={Logo} height="60px" />
                    </Link>
                    {/* Navigation bar menus */}
                    <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#main-navbar">
                        <span className="navbar-toggler-icon"></span>
                    </button>
                    <div className="collapse navbar-collapse" id="main-navbar">
                        <div className="navbar-nav ms-auto">

                            <Link to="/booking" className="link">
                                <div className="nav-link active">Booking</div>
                            </Link>
                            <Link to="/brands" className="link">
                                <div className="nav-link active">Brands</div>
                            </Link>

                            <Link to="/about" className="link">
                                <div className="nav-link active">About</div>
                            </Link>
                            <Link to="/services" className="link">
                                <div className="nav-link active">Services</div>
                            </Link>
                            <Link to="/contact" className="link">
                                <div className="nav-link active">Contact us</div>
                            </Link>
                            <Link to="/cottages" className="link">
                                <div className="nav-link active">Cottages</div>
                            </Link>
                            {/* Sign in page link on navigation bar */}
                            <Link to="/Signin" className="link">
                                <div className="nav-link active">Sign In</div>
                            </Link>
                            <div className='srch'>
                                <input type="text" class="input" placeholder="Search..." />
                                <button id="search-btn"><FaSearch /></button>
                            </div>
                        </div>
                         {/* Products cart or wishlist*/}
                        <Link to="/cart">
                            <ButtonContainer className="cart-icon">
                              <FaRegHeart />
                            <span className="d-md-none d-xs-block ms-2 title">Wishlist</span>
                            </ButtonContainer>
                        </Link>
                    </div>
                </div>
            </nav> 
            </div>
        );
    }
}

export default Navbar;
