import React, { useState, useRef } from 'react';
import Button from 'react-bootstrap/Button';
import Overlay from 'react-bootstrap/Overlay';
import Popover from 'react-bootstrap/Popover';
import './UserActions.css'
import {FaUser} from 'react-icons/fa'
function UserActions() {
    const [show, setShow] = useState(false);
    const [target, setTarget] = useState(null);
    const ref = useRef(null);

    const handleClick = (event) => {
      setShow(!show);
      setTarget(event.target);
    };

  return (
    <div>
         <div ref={ref}>
      <Button onMouseEnter={handleClick}><FaUser /></Button>

      <Overlay
        show={show}
        target={target}
        placement="bottom"
        container={ref}
        containerPadding={20}
      >
        <Popover id="popover-contained user">
          <Popover.Header as="h3">Settings</Popover.Header>
          <Popover.Body>
            <ul className='user-menu'>
                <li className='user-list'>Sign in</li>
                <li className='user-list'>Sign Up</li>
                <li className='user-list'>Learn more</li>
            </ul>
          </Popover.Body>
        </Popover>
      </Overlay>
    </div>

    </div>
  )
}

export default UserActions
