import React, { Component } from 'react';
import Product from './Product';
import Title from './Title';
import {ProductConsumer} from '../Context'
import {Link} from 'react-router-dom';
import {Helmet} from 'react-helmet';
import Image1 from '../products/13.jpg'
import Image2 from '../products/10.jpg'
import Image3 from '../products/03.jpg'


const title = 'Home';
class ProductList extends Component {
    render() {
        return (
            <React.Fragment>

            <Helmet>
                <title>Home</title>
            </Helmet>
                {/* Carousel */}
                <div id="carouselExampleControls" className="carousel slide" data-bs-ride="carousel">
                    <div className="carousel-inner">
                        <div className="carousel-item ">
                            <img src= {Image3} className="d-block w-100" alt="slider1"/>
                        </div>
                        <div className="carousel-item active">
                            <img src={Image2} className="d-block w-100" alt="slider2"/>
                        </div>
                        <div className="carousel-item">
                            <img src={Image1} className="d-block w-100" alt="slider3"/>
                        </div>
                    </div>
                    <button className="carousel-control-prev" type="button" data-bs-target="#carouselExampleControls" data-bs-slide="prev">
                        <span className="carousel-control-prev-icon" aria-hidden="true"></span>
                        <span className="visually-hidden">Previous</span>
                    </button>
                    <button className="carousel-control-next" type="button" data-bs-target="#carouselExampleControls" data-bs-slide="next">
                        <span className="carousel-control-next-icon" aria-hidden="true"></span>
                        <span className="visually-hidden">Next</span>
                    </button>
                </div>

                <div className="py-5">
                    <div className="container">

                        <Title name="our" title="products"/>
                        <div className="row justify-content-center products-container">


                            <ProductConsumer>

                                {value => {

                                    return value.products.map(product => {
                                        return <Product key={product.id} product={product}/>
                                    })
                                }}
                            </ProductConsumer>
                        </div>
                    </div>
                </div>
            </React.Fragment>
            // <div>
            //     <Product/>
            // </div>
        );
    }
}

export default ProductList;
