import React, { useState, useRef } from 'react';
import Button from 'react-bootstrap/Button';
import Overlay from 'react-bootstrap/Overlay';
import Popover from 'react-bootstrap/Popover';
import './UserActions.css'
import { Link } from 'react-router-dom';

function Services() {
  const [show, setShow] = useState(false);
  const [target, setTarget] = useState(null);
  const ref = useRef(null);
  const handleClose = (event) => setShow(false);
  const handleClick = (event) => {
    setShow(!show);
    setTarget(event.target);
  };

  return (
    <div>
        <div ref={ref}>
      <li className='user-btn' 
      onMouseEnter={handleClick} onMouseLeave={handleClose}><FaUser />

      <Overlay
        show={show}
        target={target}
        placement="bottom"
        container={ref}
        containerPadding={20}
      >
        <Popover id="popover-contained user">
          <Popover.Header as="h3"></Popover.Header>
          <Popover.Body>
            <ul className='user-menu'>
                 <Link to=""> <li className='user-list'>Edit Profile</li></Link>
                 <Link to=""><li className='user-list'>Sign in</li></Link>
                 <Link to=""><li className='user-list'>Sign Up</li></Link>

            </ul>
          </Popover.Body>
        </Popover>
      </Overlay>
      </li>
    </div>

    </div>
  )
}

export default Services
